CREATE DATABASE IF NOT EXISTS restaurant_app; USE restaurant_app; CREATE TABLE restaurants(id INT AUTO_INCREMENT PRIMARY KEY,name VARCHAR(200),city VARCHAR(100)); CREATE TABLE menu_items(id INT AUTO_INCREMENT PRIMARY KEY,restaurant_id INT,dish_name VARCHAR(200),price INT,FOREIGN KEY (restaurant_id) REFERENCES restaurants(id)); CREATE TABLE orders(id INT AUTO_INCREMENT PRIMARY KEY,menu_item_id INT,FOREIGN KEY (menu_item_id) REFERENCES menu_items(id));